mapper.select:
SELECT * 
FROM permission a
WHERE id = #{id}

mapper.update:
UPDATE permission a SET
	a.is_enable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.select:
   
		SELECT * 
     	FROM permission a
     	WHERE 1 = 1
		
			AND a.app_id = #{appId}
		
		
			AND a.is_enable = #{isEnable} 
		
     	ORDER BY a.sort DESC

mapper.delete:
DELETE FROM permission

	
		WHERE 1 != 1
	
	
		WHERE `id` = #{id}
	
	
		WHERE `id` in #{id}

DELETE FROM permission
WHERE 1 = 1

	AND app_id in #{item}
