mapper.select:
   
		SELECT * 
     	FROM user_role a
     	WHERE 1 = 1
     	
			AND a.user_id = #{userId}
		
		
			AND a.role_id = #{roleId}

mapper.insert:
INSERT INTO user_role(
	user_id,
	role_id
) 
VALUES (
	#{userId},
	#{roleId}
)

mapper.delete:
DELETE FROM user_role
WHERE 1 = 1

	AND role_id in #{item}

DELETE FROM user_role
WHERE 1 = 1

	AND user_id in #{item}
